import React, { createContext, useContext, useState, useEffect, ReactNode, useCallback } from 'react';
import { useAuth } from './AuthContext';
import { chatService, ChatUser, Message } from '../services/chatService';

interface ChatContextType {
  activeUsers: ChatUser[];
  messagesByConversation: { [key: string]: Message[] };
  isLoadingUsers: boolean;
  isLoadingMessages: boolean;
  selectedUser: ChatUser | null;
  setSelectedUser: (user: ChatUser | null) => void;
  loadMessages: (userId: string) => Promise<void>;
  refreshActiveUsers: () => Promise<void>;
  sendMessage: (userId: string, content: string) => Promise<void>;
  addMessage: (message: Message) => void;
  markAsRead: (userId: string) => Promise<void>;
}

interface ChatData {
  activeUsers: ChatUser[];
  messagesByConversation: { [key: string]: Message[] };
  lastUsersFetch: number;
  lastMessagesFetch: { [key: string]: number };
}

const CACHE_DURATION = 2 * 60 * 1000; // 2 minutes for chat data

const ChatContext = createContext<ChatContextType | undefined>(undefined);

interface ChatProviderProps {
  children: ReactNode;
}

export const ChatProvider: React.FC<ChatProviderProps> = ({ children }) => {
  const { user, token } = useAuth();
  const [chatData, setChatData] = useState<ChatData>({
    activeUsers: [],
    messagesByConversation: {},
    lastUsersFetch: 0,
    lastMessagesFetch: {}
  });
  const [isLoadingUsers, setIsLoadingUsers] = useState(false);
  const [isLoadingMessages, setIsLoadingMessages] = useState(false);
  const [selectedUser, setSelectedUser] = useState<ChatUser | null>(null);
  // Preload active users when context initializes
  useEffect(() => {
    if (user && token) {
      refreshActiveUsers();
    }
  }, [user, token, refreshActiveUsers]);

  const refreshActiveUsers = useCallback(async () => {
    if (!user) return;

    try {
      // Check cache validity
      const now = Date.now();
      if (now - chatData.lastUsersFetch < CACHE_DURATION && chatData.activeUsers.length > 0) {
        return;
      }

      setIsLoadingUsers(true);
      const users = await chatService.getActiveUsers();
      const filteredUsers = users.filter(u => u._id !== user._id);
      
      setChatData(prev => ({
        ...prev,
        activeUsers: filteredUsers,
        lastUsersFetch: now
      }));

      // Auto-select first user if no user is selected
      if (!selectedUser && filteredUsers.length > 0) {
        setSelectedUser(filteredUsers[0]);
      }
    } catch (error) {
      console.error('Error loading active users:', error);
    } finally {
      setIsLoadingUsers(false);
    }
  }, [user, chatData.lastUsersFetch, chatData.activeUsers.length, selectedUser]);

  const loadMessages = useCallback(async (userId: string) => {
    try {
      // Check cache validity
      const now = Date.now();
      const lastFetch = chatData.lastMessagesFetch[userId] || 0;
      if (now - lastFetch < CACHE_DURATION && chatData.messagesByConversation[userId]?.length > 0) {
        return;
      }

      setIsLoadingMessages(true);
      const messages = await chatService.getMessages(userId);
      const sortedMessages = messages.sort((a, b) => 
        new Date(a.timestamp).getTime() - new Date(b.timestamp).getTime()
      );
      
      setChatData(prev => ({
        ...prev,
        messagesByConversation: {
          ...prev.messagesByConversation,
          [userId]: sortedMessages
        },
        lastMessagesFetch: {
          ...prev.lastMessagesFetch,
          [userId]: now
        }
      }));
    } catch (error) {
      console.error('Error loading messages:', error);
    } finally {
      setIsLoadingMessages(false);
    }
  }, [chatData.lastMessagesFetch, chatData.messagesByConversation]);

  const sendMessage = useCallback(async (userId: string, content: string) => {
    try {
      const newMessage = await chatService.sendMessage(userId, content);
      
      // Add message to local state immediately for better UX
      setChatData(prev => ({
        ...prev,
        messagesByConversation: {
          ...prev.messagesByConversation,
          [userId]: [...(prev.messagesByConversation[userId] || []), newMessage]
        }
      }));
    } catch (error) {
      console.error('Error sending message:', error);
      throw error;
    }
  }, []);

  const addMessage = useCallback((message: Message) => {
    const otherUserId = message.sender._id === user?._id ? message.receiver._id : message.sender._id;
    
    setChatData(prev => ({
      ...prev,
      messagesByConversation: {
        ...prev.messagesByConversation,
        [otherUserId]: [...(prev.messagesByConversation[otherUserId] || []), message]
      }
    }));
  }, [user?._id]);

  const markAsRead = useCallback(async (userId: string) => {
    try {
      await chatService.markAsRead(userId);
      
      // Update local messages to mark as read
      setChatData(prev => ({
        ...prev,
        messagesByConversation: {
          ...prev.messagesByConversation,
          [userId]: (prev.messagesByConversation[userId] || []).map(msg => ({
            ...msg,
            isRead: msg.receiver._id === user?._id ? true : msg.isRead
          }))
        }
      }));
    } catch (error) {
      console.error('Error marking messages as read:', error);
    }
  }, [user?._id]);

  const value: ChatContextType = {
    activeUsers: chatData.activeUsers,
    messagesByConversation: chatData.messagesByConversation,
    isLoadingUsers,
    isLoadingMessages,
    selectedUser,
    setSelectedUser,
    loadMessages,
    refreshActiveUsers,
    sendMessage,
    addMessage,
    markAsRead
  };

  return (
    <ChatContext.Provider value={value}>
      {children}
    </ChatContext.Provider>
  );
};

export const useChat = (): ChatContextType => {
  const context = useContext(ChatContext);
  if (context === undefined) {
    throw new Error('useChat must be used within a ChatProvider');
  }
  return context;
};
