import React, { useState, useEffect } from 'react';
import { 
  Card, 
  Form, 
  Input, 
  Button, 
  Avatar, 
  Upload, 
  Row, 
  Col, 
  Typography, 
  Divider,
  DatePicker,
  Select,
  App
} from 'antd';
import { 
  UserOutlined, 
  SaveOutlined,
  CameraOutlined,
  PhoneOutlined,
  EnvironmentOutlined
} from '@ant-design/icons';
import { useAuth } from '../../contexts/AuthContext';
import { userAPI } from '../../services/api';
import { User } from '../../types';
import dayjs from 'dayjs';
import './Settings.css';

const { Title, Text } = Typography;
const { Option } = Select;

const SettingsContent: React.FC = () => {
  const { user, login, token } = useAuth();
  const { message } = App.useApp();
  const [form] = Form.useForm();
  const [loading, setLoading] = useState(false);
  const [avatarLoading, setAvatarLoading] = useState(false);
  const [profileData, setProfileData] = useState<User | null>(null);

  useEffect(() => {
    if (user) {
      setProfileData(user);
      form.setFieldsValue({
        username: user.username,
        email: user.email,
        gender: user.gender || '',
        birthDate: user.birthDate ? dayjs(user.birthDate) : null,
        phone: user.phone || '',
        address: user.address || ''
      });
    }
  }, [user, form]);

  const handleAvatarChange = async (info: any) => {
    if (info.file.status === 'uploading') {
      setAvatarLoading(true);
      return;
    }

    if (info.file.status === 'done' || info.file.originFileObj) {
      const file = info.file.originFileObj || info.file;
      
      // Validate file size (max 1MB)
      if (file.size > 1024 * 1024) {
        message.error('Kích thước ảnh không được vượt quá 1MB!');
        setAvatarLoading(false);
        return;
      }

      // Convert to base64
      const reader = new FileReader();
      reader.onload = async () => {
        try {
          const base64 = reader.result as string;
          await userAPI.updateProfile({ avatar: base64 });
          
          // Update user context
          if (user) {
            const updatedUser = { ...user, avatar: base64 };
            login(updatedUser, token!);
            setProfileData(updatedUser);
          }
          
          message.success('Cập nhật avatar thành công!');
        } catch (error) {
          message.error('Không thể cập nhật avatar!');
        } finally {
          setAvatarLoading(false);
        }
      };
      reader.readAsDataURL(file);
    }
  };

  const handleProfileUpdate = async (values: any) => {
    try {
      setLoading(true);
      
      // Only send allowed fields for regular users
      const updateData: any = {
        username: values.username
      };

      // Admin can update additional fields
      if (user?.role === 'admin') {
        updateData.email = values.email;
        updateData.gender = values.gender;
        updateData.birthDate = values.birthDate ? values.birthDate.toISOString() : null;
        updateData.phone = values.phone;
        updateData.address = values.address;
      }

      await userAPI.updateProfile(updateData);
      
      // Update user context
      if (user) {
        const updatedUser = { ...user, ...updateData };
        login(updatedUser, token!);
        setProfileData(updatedUser);
      }
      
      message.success('Cập nhật thông tin thành công!');
    } catch (error: any) {
      message.error(error.response?.data?.message || 'Không thể cập nhật thông tin!');
    } finally {
      setLoading(false);
    }
  };

  const beforeUpload = (file: File) => {
    const isImage = file.type.startsWith('image/');
    if (!isImage) {
      message.error('Chỉ được tải lên file ảnh!');
      return false;
    }
    
    const isLt1M = file.size / 1024 / 1024 < 1;
    if (!isLt1M) {
      message.error('Kích thước ảnh phải nhỏ hơn 1MB!');
      return false;
    }
    
    return true;
  };

  if (!profileData) {
    return <div>Loading...</div>;
  }

  return (
    <div className="settings-container">
      <Card 
        title={
          <div className="settings-title">
            <UserOutlined className="title-icon" />
            <Title level={3} style={{ margin: 0 }}>Thông tin cá nhân</Title>
          </div>
        }
        className="settings-card"
      >
        <Row gutter={24}>
          {/* Avatar Section */}
          <Col xs={24} md={8} lg={6} className="avatar-section">
            <div className="avatar-container">
              <Avatar
                size={120}
                src={profileData.avatar}
                icon={<UserOutlined />}
                className="user-avatar"
              />
              <Upload
                showUploadList={false}
                beforeUpload={beforeUpload}
                onChange={handleAvatarChange}
                accept="image/*"
              >
                <Button
                  type="dashed"
                  icon={<CameraOutlined />}
                  loading={avatarLoading}
                  className="upload-button"
                >
                  Thay đổi ảnh
                </Button>
              </Upload>
            </div>
          </Col>

          {/* Form Section */}
          <Col xs={24} md={16} lg={18}>
            <Form
              form={form}
              layout="vertical"
              onFinish={handleProfileUpdate}
              className="settings-form"
            >
              <Row gutter={16}>
                <Col xs={24} md={12}>
                  <Form.Item
                    label="Tên đăng nhập"
                    name="username"
                    rules={[
                      { required: true, message: 'Vui lòng nhập tên đăng nhập!' },
                      { min: 6, message: 'Tên đăng nhập phải có ít nhất 6 ký tự!' },
                      { max: 20, message: 'Tên đăng nhập không được quá 20 ký tự!' }
                    ]}
                  >
                    <Input prefix={<UserOutlined />} />
                  </Form.Item>
                </Col>

                <Col xs={24} md={12}>
                  <Form.Item
                    label="Email"
                    name="email"
                    rules={[
                      { required: true, message: 'Vui lòng nhập email!' },
                      { type: 'email', message: 'Email không hợp lệ!' }
                    ]}
                  >
                    <Input 
                      prefix={<UserOutlined />} 
                      disabled={user?.role !== 'admin'}
                      placeholder={user?.role !== 'admin' ? 'Chỉ admin mới có thể thay đổi' : ''}
                    />
                  </Form.Item>
                </Col>
              </Row>

              {user?.role === 'admin' && (
                <>
                  <Row gutter={16}>
                    <Col xs={24} md={12}>
                      <Form.Item
                        label="Giới tính"
                        name="gender"
                      >
                        <Select placeholder="Chọn giới tính">
                          <Option value="male">Nam</Option>
                          <Option value="female">Nữ</Option>
                          <Option value="other">Khác</Option>
                        </Select>
                      </Form.Item>
                    </Col>

                    <Col xs={24} md={12}>
                      <Form.Item
                        label="Ngày sinh"
                        name="birthDate"
                      >
                        <DatePicker 
                          style={{ width: '100%' }} 
                          placeholder="Chọn ngày sinh"
                          format="DD/MM/YYYY"
                        />
                      </Form.Item>
                    </Col>
                  </Row>

                  <Row gutter={16}>
                    <Col xs={24} md={12}>
                      <Form.Item
                        label="Số điện thoại"
                        name="phone"
                        rules={[
                          { pattern: /^[0-9]{10,11}$/, message: 'Số điện thoại phải có 10-11 chữ số!' }
                        ]}
                      >
                        <Input prefix={<PhoneOutlined />} placeholder="Nhập số điện thoại" />
                      </Form.Item>
                    </Col>

                    <Col xs={24} md={12}>
                      <Form.Item
                        label="Địa chỉ"
                        name="address"
                        rules={[
                          { max: 200, message: 'Địa chỉ không được quá 200 ký tự!' }
                        ]}
                      >
                        <Input prefix={<EnvironmentOutlined />} placeholder="Nhập địa chỉ" />
                      </Form.Item>
                    </Col>
                  </Row>
                </>
              )}

              <Divider />

              <Form.Item>
                <Button 
                  type="primary" 
                  htmlType="submit" 
                  icon={<SaveOutlined />}
                  loading={loading}
                  size="large"
                >
                  Lưu thay đổi
                </Button>
              </Form.Item>
            </Form>
          </Col>
        </Row>
      </Card>
    </div>
  );
};

// Main Settings Component with App wrapper
const Settings: React.FC = () => {
  return (
    <App>
      <SettingsContent />
    </App>
  );
};

export default Settings;
