import React, { useState, useEffect } from 'react';
import { 
  Card, 
  Typography, 
  Input, 
  Button, 
  List, 
  Avatar, 
  Space,
  Divider,
  Badge,
  Row,
  Col 
} from 'antd';
import { 
  SendOutlined, 
  UserOutlined,
  MessageOutlined 
} from '@ant-design/icons';
import { useAuth } from '../../contexts/AuthContext';
import './MessengerChatPage.css';

const { Title, Text } = Typography;
const { TextArea } = Input;

interface ChatMessage {
  id: string;
  content: string;
  senderId: string;
  senderName: string;
  timestamp: Date;
  type: 'text' | 'image' | 'file';
}

interface ChatUser {
  id: string;
  username: string;
  avatar?: string;
  isOnline: boolean;
  lastSeen?: Date;
}

const MessengerChatPage: React.FC = () => {
  const { user } = useAuth();
  const [message, setMessage] = useState('');
  const [messages, setMessages] = useState<ChatMessage[]>([]);
  const [activeUsers, setActiveUsers] = useState<ChatUser[]>([]);
  const [selectedUser, setSelectedUser] = useState<ChatUser | null>(null);

  // Mock data for demonstration
  useEffect(() => {
    const mockUsers: ChatUser[] = [
      {
        id: '1',
        username: 'John Doe',
        isOnline: true,
      },
      {
        id: '2',
        username: 'Jane Smith',
        isOnline: true,
      },
      {
        id: '3',
        username: 'Mike Johnson',
        isOnline: false,
        lastSeen: new Date(Date.now() - 1000 * 60 * 30), // 30 minutes ago
      },
    ];

    const mockMessages: ChatMessage[] = [
      {
        id: '1',
        content: 'Hello! How are you doing?',
        senderId: '1',
        senderName: 'John Doe',
        timestamp: new Date(Date.now() - 1000 * 60 * 10), // 10 minutes ago
        type: 'text',
      },      {
        id: '2',
        content: 'I\'m doing great, thanks for asking!',
        senderId: user?._id || 'current-user',
        senderName: user?.username || 'You',
        timestamp: new Date(Date.now() - 1000 * 60 * 5), // 5 minutes ago
        type: 'text',
      },
    ];

    setActiveUsers(mockUsers);
    setMessages(mockMessages);
    setSelectedUser(mockUsers[0]);
  }, [user]);

  const handleSendMessage = () => {
    if (!message.trim() || !selectedUser) return;

    const newMessage: ChatMessage = {
      id: Date.now().toString(),
      content: message.trim(),
      senderId: user?.id || 'current-user',
      senderName: user?.username || 'You',
      timestamp: new Date(),
      type: 'text',
    };

    setMessages(prev => [...prev, newMessage]);
    setMessage('');
  };

  const handleKeyPress = (e: React.KeyboardEvent) => {
    if (e.key === 'Enter' && !e.shiftKey) {
      e.preventDefault();
      handleSendMessage();
    }
  };

  const formatTime = (date: Date) => {
    return date.toLocaleTimeString('vi-VN', { 
      hour: '2-digit', 
      minute: '2-digit' 
    });
  };

  const filteredMessages = selectedUser 
    ? messages.filter(msg => 
        msg.senderId === selectedUser.id || msg.senderId === (user?.id || 'current-user')
      )
    : [];

  return (
    <div className="messenger-chat-page">
      <Row gutter={16} style={{ height: '100%' }}>
        {/* Users List */}
        <Col xs={24} sm={8} md={6} lg={6} xl={5}>
          <Card 
            title={
              <Space>
                <MessageOutlined />
                <span>Conversations</span>
              </Space>
            }
            className="users-card"
            bodyStyle={{ padding: 0 }}
          >
            <List
              dataSource={activeUsers}
              renderItem={(chatUser) => (
                <List.Item
                  className={`user-item ${selectedUser?.id === chatUser.id ? 'selected' : ''}`}
                  onClick={() => setSelectedUser(chatUser)}
                >
                  <List.Item.Meta
                    avatar={
                      <Badge 
                        dot 
                        status={chatUser.isOnline ? 'success' : 'default'}
                        offset={[-2, 32]}
                      >
                        <Avatar 
                          icon={<UserOutlined />} 
                          src={chatUser.avatar}
                          size="large"
                        />
                      </Badge>
                    }
                    title={<Text strong>{chatUser.username}</Text>}
                    description={
                      chatUser.isOnline 
                        ? <Text type="success">Online</Text>
                        : chatUser.lastSeen 
                          ? <Text type="secondary">Last seen {formatTime(chatUser.lastSeen)}</Text>
                          : <Text type="secondary">Offline</Text>
                    }
                  />
                </List.Item>
              )}
            />
          </Card>
        </Col>

        {/* Chat Area */}
        <Col xs={24} sm={16} md={18} lg={18} xl={19}>
          {selectedUser ? (
            <Card 
              className="chat-card"
              title={
                <Space>
                  <Avatar icon={<UserOutlined />} src={selectedUser.avatar} />
                  <div>
                    <Text strong>{selectedUser.username}</Text>
                    <br />
                    <Text type="secondary" style={{ fontSize: '12px' }}>
                      {selectedUser.isOnline ? 'Online' : 'Offline'}
                    </Text>
                  </div>
                </Space>
              }
              bodyStyle={{ 
                padding: 0, 
                display: 'flex', 
                flexDirection: 'column',
                height: 'calc(100vh - 200px)'
              }}
            >
              {/* Messages Area */}
              <div className="messages-container">
                <List
                  dataSource={filteredMessages}
                  renderItem={(msg) => (
                    <List.Item
                      className={`message-item ${
                        msg.senderId === (user?.id || 'current-user') ? 'own-message' : 'other-message'
                      }`}
                    >
                      <div className="message-content">
                        <div className="message-bubble">
                          <Text>{msg.content}</Text>
                        </div>
                        <Text type="secondary" className="message-time">
                          {formatTime(msg.timestamp)}
                        </Text>
                      </div>
                    </List.Item>
                  )}
                />
              </div>

              <Divider style={{ margin: 0 }} />

              {/* Message Input */}
              <div className="message-input-container">
                <Space.Compact style={{ width: '100%' }}>
                  <TextArea
                    value={message}
                    onChange={(e) => setMessage(e.target.value)}
                    onKeyPress={handleKeyPress}
                    placeholder="Type a message..."
                    autoSize={{ minRows: 1, maxRows: 4 }}
                    style={{ resize: 'none' }}
                  />
                  <Button
                    type="primary"
                    icon={<SendOutlined />}
                    onClick={handleSendMessage}
                    disabled={!message.trim()}
                  >
                    Send
                  </Button>
                </Space.Compact>
              </div>
            </Card>
          ) : (
            <Card className="no-chat-selected">
              <div style={{ textAlign: 'center', padding: '60px 20px' }}>
                <MessageOutlined style={{ fontSize: '48px', color: '#d9d9d9' }} />
                <Title level={4} type="secondary">Select a conversation to start chatting</Title>
                <Text type="secondary">
                  Choose from your existing conversations on the left or start a new one.
                </Text>
              </div>
            </Card>
          )}
        </Col>
      </Row>
    </div>
  );
};

export default MessengerChatPage;
