import api from './api';

export interface Message {
  _id: string;
  content: string;
  sender: string;
  receiver: string;
  messageType: 'text' | 'image' | 'file';
  timestamp: Date;
  read: boolean;
}

export interface ChatUser {
  _id: string;
  username: string;
  avatar?: string;
  isOnline: boolean;
  lastSeen?: Date;
}

export const chatService = {
  getMessages: async (receiverId: string): Promise<Message[]> => {
    const response = await api.get(`/v1/chat/messages/${receiverId}`);
    return response.data;
  },

  sendMessage: async (receiverId: string, content: string, messageType: string = 'text'): Promise<Message> => {
    const response = await api.post('/v1/chat/send', {
      receiverId,
      content,
      messageType
    });
    return response.data;
  },

  markAsRead: async (messageId: string): Promise<void> => {
    await api.put(`/v1/chat/read/${messageId}`);
  },

  getActiveUsers: async (): Promise<ChatUser[]> => {
    const response = await api.get('/v1/chat/users/active');
    return response.data;
  },

  getConversations: async (): Promise<{
    user: ChatUser;
    lastMessage: Message | null;
    unreadCount: number;
  }[]> => {
    const response = await api.get('/v1/chat/conversations');
    return response.data;
  }
};

export default chatService;
