import React, { useState, useEffect, useRef } from 'react';
import { 
  Card, 
  Typography, 
  Input, 
  Button, 
  List, 
  Avatar, 
  Space,
  Divider,
  Badge,
  Row,
  Col,
  message 
} from 'antd';
import { 
  SendOutlined, 
  UserOutlined,
  MessageOutlined 
} from '@ant-design/icons';
import { useAuth } from '../../contexts/AuthContext';
import { Message, User } from '../../types';
import { chatService } from '../../services/chatService';
import { socketService } from '../../services/socketService';
import './MessengerChatPage.css';

const { Title, Text } = Typography;
const { TextArea } = Input;

const MessengerChatPage: React.FC = () => {
  const { user, token } = useAuth();
  const [messageText, setMessageText] = useState('');
  const [messages, setMessages] = useState<Message[]>([]);
  const [activeUsers, setActiveUsers] = useState<User[]>([]);
  const [selectedUser, setSelectedUser] = useState<User | null>(null);
  const [loading, setLoading] = useState(false);
  const messagesEndRef = useRef<HTMLDivElement>(null);

  useEffect(() => {
    if (user && token) {
      // Kết nối socket
      const socket = socketService.connect(token);

      // Thiết lập lắng nghe tin nhắn mới
      socket.on('new_message', (newMessage: Message) => {
        if (
          selectedUser && 
          (newMessage.sender === selectedUser._id || newMessage.receiver === selectedUser._id)
        ) {
          setMessages(prev => [...prev, newMessage]);
          scrollToBottom();
        }
      });

      // Lắng nghe trạng thái người dùng
      socket.on('user_online', (userId: string) => {
        setActiveUsers(prev => 
          prev.map(u => u._id === userId ? { ...u, isOnline: true } : u)
        );
      });

      socket.on('user_offline', (userId: string) => {
        setActiveUsers(prev => 
          prev.map(u => u._id === userId ? { ...u, isOnline: false } : u)
        );
      });

      // Lấy danh sách người dùng đang hoạt động
      fetchActiveUsers();

      return () => {
        socketService.disconnect();
      };
    }
  }, [user, token, selectedUser]);

  useEffect(() => {
    if (selectedUser) {
      fetchMessages(selectedUser._id);
    }
  }, [selectedUser]);

  const fetchActiveUsers = async () => {
    try {
      const users = await chatService.getActiveUsers();
      setActiveUsers(users.filter((u: User) => u._id !== user?._id));
    } catch (error) {
      message.error('Không thể tải danh sách người dùng');
    }
  };

  const fetchMessages = async (userId: string) => {
    try {
      setLoading(true);
      const response = await chatService.getMessages(userId);
      setMessages(response.messages);
      scrollToBottom();
    } catch (error) {
      message.error('Không thể tải tin nhắn');
    } finally {
      setLoading(false);
    }
  };

  const handleSendMessage = async () => {
    if (!messageText.trim() || !selectedUser || !user) return;

    try {
      const newMessage = await chatService.sendMessage(selectedUser._id, messageText.trim());
      
      // Gửi thông báo qua socket
      socketService.sendMessage(selectedUser._id, messageText.trim());
      
      setMessages(prev => [...prev, newMessage]);
      setMessageText('');
      scrollToBottom();
    } catch (error) {
      message.error('Không thể gửi tin nhắn');
    }
  };

  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  };

  return (
    <div className="chat-page">
      <Row gutter={[16, 16]} className="chat-container">
        <Col xs={24} sm={8} md={6} className="users-list">
          <Card className="users-card">
            <Title level={4}>Người dùng trực tuyến</Title>
            <List
              dataSource={activeUsers}
              renderItem={(item: User) => (
                <List.Item
                  onClick={() => setSelectedUser(item)}
                  className={`user-item ${selectedUser?._id === item._id ? 'active' : ''}`}
                >
                  <List.Item.Meta
                    avatar={
                      <Badge
                        dot
                        status={item.isOnline ? 'success' : 'default'}
                        offset={[-5, 28]}
                      >
                        <Avatar
                          size="large"
                          icon={<UserOutlined />}
                          src={item.avatar}
                        />
                      </Badge>
                    }
                    title={item.username}
                  />
                </List.Item>
              )}
            />
          </Card>
        </Col>

        <Col xs={24} sm={16} md={18} className="chat-messages">
          <Card className="messages-card">
            {selectedUser ? (
              <>
                <div className="chat-header">
                  <Space align="center">
                    <Badge
                      dot
                      status={selectedUser.isOnline ? 'success' : 'default'}
                      offset={[-5, 28]}
                    >
                      <Avatar
                        size="large"
                        icon={<UserOutlined />}
                        src={selectedUser.avatar}
                      />
                    </Badge>
                    <Title level={4} style={{ margin: 0 }}>
                      {selectedUser.username}
                    </Title>
                  </Space>
                </div>

                <Divider style={{ margin: '12px 0' }} />

                <div className="messages-container">
                  <List
                    className="messages-list"
                    loading={loading}
                    dataSource={messages}
                    renderItem={(msg: Message) => (
                      <List.Item
                        className={`message-item ${
                          msg.sender === user?._id ? 'sent' : 'received'
                        }`}
                      >
                        <div className="message-content">
                          <Text>{msg.content}</Text>
                          <div className="message-time">
                            {new Date(msg.timestamp).toLocaleTimeString()}
                          </div>
                        </div>
                      </List.Item>
                    )}
                  />
                  <div ref={messagesEndRef} />
                </div>

                <div className="chat-input">
                  <TextArea
                    value={messageText}
                    onChange={e => setMessageText(e.target.value)}
                    onPressEnter={e => {
                      if (!e.shiftKey) {
                        e.preventDefault();
                        handleSendMessage();
                      }
                    }}
                    placeholder="Nhập tin nhắn..."
                    autoSize={{ minRows: 1, maxRows: 4 }}
                  />
                  <Button
                    type="primary"
                    icon={<SendOutlined />}
                    onClick={handleSendMessage}
                    disabled={!messageText.trim()}
                  />
                </div>
              </>
            ) : (
              <div className="no-chat-selected">
                <MessageOutlined style={{ fontSize: 48, opacity: 0.5 }} />
                <Text type="secondary">
                  Chọn một người dùng để bắt đầu trò chuyện
                </Text>
              </div>
            )}
          </Card>
        </Col>
      </Row>
    </div>
  );
};

export default MessengerChatPage;
