import { io, Socket } from 'socket.io-client';
import { Message, ChatUser } from './chatService';

type MessageCallback = (message: Message) => void;
type UserStatusCallback = (userId: string) => void;
type TypingCallback = (data: { userId: string, isTyping: boolean }) => void;

class SocketService {
  private socket: Socket | null = null;
  private messageCallbacks: MessageCallback[] = [];
  private userStatusCallbacks: UserStatusCallback[] = [];
  private typingCallbacks: TypingCallback[] = [];
  private heartbeatInterval: NodeJS.Timeout | null = null;connect(token: string) {
    if (!this.socket || this.socket.disconnected) {
      this.socket = io(process.env.REACT_APP_API_URL || 'http://localhost:8000', {
        auth: { token },
        transports: ['websocket'],
        reconnection: true,
        reconnectionDelay: 500,
        reconnectionDelayMax: 2000,
        reconnectionAttempts: 20,
        forceNew: true,
        timeout: 10000,
        // Handle tab switching better
        closeOnBeforeunload: false
      });

      this.socket.on('connect', () => {
        console.log('Socket connected');
      });

      this.socket.on('reconnect', (attemptNumber) => {
        console.log('Socket reconnected after', attemptNumber, 'attempts');
      });

      this.socket.on('reconnect_attempt', (attemptNumber) => {
        console.log('Reconnection attempt:', attemptNumber);
      });

      this.socket.on('connect_error', (error: Error) => {
        console.error('Socket connection error:', error);
      });      this.socket.on('disconnect', (reason) => {
        console.log('Socket disconnected:', reason);
        // Don't immediately show as disconnected for certain reasons that might be temporary
        if (reason === 'transport close' || 
            reason === 'transport error' || 
            reason === 'ping timeout') {
          console.log('Temporary disconnect, socket will try to reconnect...');
          // Don't set disconnected state immediately - wait for reconnection attempts
        } else {
          console.log('Permanent disconnect:', reason);
        }
      });      this.socket.on('reconnect_failed', () => {
        console.error('Socket failed to reconnect after all attempts');
      });

      this.socket.on('reconnect_error', (error) => {
        console.error('Socket reconnection error:', error);
      });

      // Message events
      this.socket.on('newMessage', (message: Message) => {
        console.log('Received new message:', message);
        this.messageCallbacks.forEach(callback => callback(message));
      });

      this.socket.on('messageConfirmed', (message: Message) => {
        console.log('Message confirmed:', message);
        this.messageCallbacks.forEach(callback => callback(message));
      });

      this.socket.on('messageError', (error: { error: string }) => {
        console.error('Message error:', error);
      });

      // User status events
      this.socket.on('userOnline', (userId: string) => {
        this.userStatusCallbacks.forEach(callback => callback(userId));
      });

      this.socket.on('userOffline', (userId: string) => {
        this.userStatusCallbacks.forEach(callback => callback(userId));
      });

      // Typing events
      this.socket.on('userTyping', (data: { userId: string, isTyping: boolean }) => {
        this.typingCallbacks.forEach(callback => callback(data));
      });
    }
    return this.socket;
  }  disconnect() {
    if (this.socket) {
      this.socket.removeAllListeners();
      this.socket.disconnect();
      this.socket = null;
    }
  }

  forceReconnect(token: string) {
    console.log('Force reconnecting socket...');
    this.disconnect();
    return this.connect(token);
  }

  isConnected() {
    return this.socket && this.socket.connected;
  }

  sendMessage(receiverId: string, content: string, messageType: string = 'text') {
    if (this.socket) {
      this.socket.emit('sendMessage', { receiverId, content, messageType });
    }
  }

  onMessage(callback: MessageCallback) {
    this.messageCallbacks.push(callback);
    return () => this.removeMessageCallback(callback);
  }

  onUserStatus(callback: UserStatusCallback) {
    this.userStatusCallbacks.push(callback);
    return () => this.removeUserStatusCallback(callback);
  }

  onTyping(callback: TypingCallback) {
    this.typingCallbacks.push(callback);
    return () => this.removeTypingCallback(callback);
  }

  sendTypingStatus(receiverId: string, isTyping: boolean) {
    if (this.socket) {
      this.socket.emit('typing', { receiverId, isTyping });
    }
  }

  markAsRead(senderId: string) {
    if (this.socket) {
      this.socket.emit('markAsRead', { senderId });
    }
  }

  off(event: string) {
    if (this.socket) {
      this.socket.off(event);
    }
  }

  getSocket() {
    return this.socket;
  }

  private removeMessageCallback(callback: MessageCallback) {
    this.messageCallbacks = this.messageCallbacks.filter(cb => cb !== callback);
  }

  private removeUserStatusCallback(callback: UserStatusCallback) {
    this.userStatusCallbacks = this.userStatusCallbacks.filter(cb => cb !== callback);
  }

  private removeTypingCallback(callback: TypingCallback) {
    this.typingCallbacks = this.typingCallbacks.filter(cb => cb !== callback);
  }
}

export const socketService = new SocketService();
export default socketService;
