import React, { useState, useEffect } from 'react';
import ReactDOM from 'react-dom';
import { 
  Card, 
  Form, 
  Input, 
  Button, 
  Avatar, 
  Upload, 
  Row, 
  Col, 
  Typography, 
  Divider,
  DatePicker,
  Select,
  Space,
  Modal,
  App
} from 'antd';
import { 
  UserOutlined, 
  SaveOutlined, 
  LockOutlined,
  CameraOutlined,
  PhoneOutlined,
  EnvironmentOutlined
} from '@ant-design/icons';
import { useAuth } from '../../contexts/AuthContext';
import { userAPI } from '../../services/api';
import { User } from '../../types';
import dayjs from 'dayjs';
import './Settings.css';

const { Title, Text } = Typography;
const { Option } = Select;

interface SettingsContentProps {
  isPasswordModalVisible: boolean;
  setIsPasswordModalVisible: (visible: boolean) => void;
  onPasswordChange: (values: any) => Promise<void>;
  loading: boolean;
}

// Separate Password Modal Component
const PasswordChangeModal: React.FC<{
  visible: boolean;
  onCancel: () => void;
  onSubmit: (values: any) => Promise<void>;
  loading: boolean;
}> = ({ visible, onCancel, onSubmit, loading }) => {
  const [form] = Form.useForm();

  useEffect(() => {
    if (!visible) {
      form.resetFields();
    }
  }, [visible, form]);

  const handleSubmit = async (values: any) => {
    await onSubmit(values);
    form.resetFields();
  };

  if (!visible) return null;

  return ReactDOM.createPortal(
    <Modal
      title={
        <div style={{ textAlign: 'center', marginBottom: '16px' }}>
          <img 
            src="/logo-evine.png" 
            alt="Evine Logo" 
            style={{ height: '40px', marginBottom: '8px' }}
          />
          <div>Đổi mật khẩu</div>
        </div>
      }
      open={visible}
      onCancel={onCancel}
      footer={null}
      width={400}
      centered
      destroyOnClose
      maskClosable
      keyboard
      getContainer={false}
      zIndex={1050}
    >
      <Form
        form={form}
        layout="vertical"
        onFinish={handleSubmit}
      >
        <Form.Item
          label="Mật khẩu hiện tại"
          name="currentPassword"
          rules={[
            { required: true, message: 'Vui lòng nhập mật khẩu hiện tại!' }
          ]}
        >
          <Input.Password prefix={<LockOutlined />} />
        </Form.Item>

        <Form.Item
          label="Mật khẩu mới"
          name="newPassword"
          rules={[
            { required: true, message: 'Vui lòng nhập mật khẩu mới!' },
            { min: 6, message: 'Mật khẩu phải có ít nhất 6 ký tự!' }
          ]}
        >
          <Input.Password prefix={<LockOutlined />} />
        </Form.Item>

        <Form.Item
          label="Xác nhận mật khẩu mới"
          name="confirmPassword"
          dependencies={['newPassword']}
          rules={[
            { required: true, message: 'Vui lòng xác nhận mật khẩu mới!' },
            ({ getFieldValue }) => ({
              validator(_, value) {
                if (!value || getFieldValue('newPassword') === value) {
                  return Promise.resolve();
                }
                return Promise.reject(new Error('Mật khẩu xác nhận không khớp!'));
              }
            })
          ]}
        >
          <Input.Password prefix={<LockOutlined />} />
        </Form.Item>

        <Form.Item>
          <Space style={{ width: '100%', justifyContent: 'flex-end' }}>
            <Button onClick={onCancel}>
              Hủy
            </Button>
            <Button type="primary" htmlType="submit" loading={loading}>
              Đổi mật khẩu
            </Button>
          </Space>
        </Form.Item>
      </Form>
    </Modal>,
    document.body
  );
};

const SettingsContent: React.FC<SettingsContentProps> = ({ 
  isPasswordModalVisible, 
  setIsPasswordModalVisible,
  onPasswordChange,
  loading
}) => {
  const { user, login, token } = useAuth();
  const { message } = App.useApp();
  const [form] = Form.useForm();
  const [profileLoading, setProfileLoading] = useState(false);
  const [avatarLoading, setAvatarLoading] = useState(false);
  const [profileData, setProfileData] = useState<User | null>(null);

  useEffect(() => {
    console.log('Settings component mounted');
  }, []);  useEffect(() => {
    console.log('isPasswordModalVisible changed:', isPasswordModalVisible);
    if (isPasswordModalVisible) {
      // Prevent body scroll lock
      document.body.style.overflow = 'auto';
      // Check if modal is actually in DOM
      setTimeout(() => {
        const modalElements = document.querySelectorAll('.ant-modal');
        console.log('Modal elements found:', modalElements.length);
        const visibleModals = document.querySelectorAll('.ant-modal:not([style*="display: none"])');
        console.log('Visible modal elements:', visibleModals.length);
      }, 100);
    } else {
      // Restore body scroll
      document.body.style.overflow = '';
    }
  }, [isPasswordModalVisible]);

  useEffect(() => {
    if (user) {
      setProfileData(user);
      form.setFieldsValue({
        username: user.username,
        email: user.email,
        gender: user.gender || '',
        birthDate: user.birthDate ? dayjs(user.birthDate) : null,
        phone: user.phone || '',
        address: user.address || ''
      });
    }
  }, [user, form]);

  useEffect(() => {
    console.log('Password modal state changed:', isPasswordModalVisible);
  }, [isPasswordModalVisible]);

  const handleAvatarChange = async (info: any) => {
    if (info.file.status === 'uploading') {
      setAvatarLoading(true);
      return;
    }

    if (info.file.status === 'done' || info.file.originFileObj) {
      const file = info.file.originFileObj || info.file;
      
      // Validate file size (max 1MB)
      if (file.size > 1024 * 1024) {
        message.error('Kích thước ảnh không được vượt quá 1MB!');
        setAvatarLoading(false);
        return;
      }

      // Convert to base64
      const reader = new FileReader();
      reader.onload = async () => {
        try {
          const base64 = reader.result as string;
          await userAPI.updateProfile({ avatar: base64 });
          
          // Update user context
          if (user) {
            const updatedUser = { ...user, avatar: base64 };
            login(updatedUser, token!);
            setProfileData(updatedUser);
          }
          
          message.success('Cập nhật avatar thành công!');
        } catch (error) {
          message.error('Không thể cập nhật avatar!');
        } finally {
          setAvatarLoading(false);
        }
      };
      reader.readAsDataURL(file);
    }
  };

  const handleProfileUpdate = async (values: any) => {
    try {
      setLoading(true);
      
      // Only send allowed fields for regular users
      const updateData: any = {
        username: values.username
      };

      // Admin can update additional fields
      if (user?.role === 'admin') {
        updateData.email = values.email;
        updateData.gender = values.gender;
        updateData.birthDate = values.birthDate ? values.birthDate.toISOString() : null;
        updateData.phone = values.phone;
        updateData.address = values.address;
      }

      await userAPI.updateProfile(updateData);
      
      // Update user context
      if (user) {
        const updatedUser = { ...user, ...updateData };
        login(updatedUser, token!);
        setProfileData(updatedUser);
      }
      
      message.success('Cập nhật thông tin thành công!');
    } catch (error: any) {
      message.error(error.response?.data?.message || 'Không thể cập nhật thông tin!');
    } finally {
      setLoading(false);
    }
  };

  const handlePasswordChange = async (values: any) => {
    try {
      setLoading(true);
      await userAPI.changePassword({
        currentPassword: values.currentPassword,
        newPassword: values.newPassword
      });
      
      message.success('Đổi mật khẩu thành công!');
      setIsPasswordModalVisible(false);
      passwordForm.resetFields();
    } catch (error: any) {
      message.error(error.response?.data?.message || 'Không thể đổi mật khẩu!');
    } finally {
      setLoading(false);
    }
  };

  const beforeUpload = (file: File) => {
    const isImage = file.type.startsWith('image/');
    if (!isImage) {
      message.error('Chỉ được tải lên file ảnh!');
      return false;
    }
    
    const isLt1M = file.size / 1024 / 1024 < 1;
    if (!isLt1M) {
      message.error('Kích thước ảnh phải nhỏ hơn 1MB!');
      return false;
    }
    
    return true;  };

  if (!profileData) {
    return <div>Loading...</div>;
  }

  console.log('SettingsContent rendering with modal visible:', isPasswordModalVisible);

  return (
    <div className="settings-page">
      <div className="settings-header">
        <Title level={2}>Cài đặt tài khoản</Title>
        <Text type="secondary">Quản lý thông tin cá nhân của bạn</Text>
      </div>

      <Row gutter={[24, 24]}>
        <Col xs={24} lg={8}>
          <Card className="avatar-card">
            <div className="avatar-section">
              <Avatar
                size={120}
                icon={<UserOutlined />}
                src={profileData.avatar}
                className="user-avatar"
              />
              <Upload
                name="avatar"
                showUploadList={false}
                beforeUpload={beforeUpload}
                onChange={handleAvatarChange}
                accept="image/*"
              >
                <Button 
                  icon={<CameraOutlined />} 
                  loading={avatarLoading}
                  className="upload-button"
                >
                  Đổi ảnh đại diện
                </Button>
              </Upload>
            </div>
            
            <Divider />
            <div className="user-info">
              <Text type="secondary">
                {profileData.role === 'admin' ? 'Quản trị viên' : 'Người dùng'}
              </Text>
            </div>
          </Card>
        </Col>

        <Col xs={24} lg={16}>
          <Card title="Thông tin cá nhân" className="profile-card">
            <Form
              form={form}
              layout="vertical"
              onFinish={handleProfileUpdate}
              initialValues={{
                username: profileData.username,
                email: profileData.email,
                gender: profileData.gender || '',
                birthDate: profileData.birthDate ? dayjs(profileData.birthDate) : null,
                phone: profileData.phone || '',
                address: profileData.address || ''
              }}
            >
              <Row gutter={16}>
                <Col xs={24} sm={12}>
                  <Form.Item
                    label="Tên đăng nhập"
                    name="username"
                    rules={[
                      { required: true, message: 'Vui lòng nhập tên đăng nhập!' },
                      { min: 6, message: 'Tên đăng nhập phải có ít nhất 6 ký tự!' },
                      { max: 20, message: 'Tên đăng nhập không được quá 20 ký tự!' }
                    ]}
                  >
                    <Input prefix={<UserOutlined />} />
                  </Form.Item>
                </Col>

                <Col xs={24} sm={12}>
                  <Form.Item
                    label="Email"
                    name="email"
                  >
                    <Input 
                      prefix={<UserOutlined />} 
                      disabled={user?.role !== 'admin'}
                      placeholder={user?.role !== 'admin' ? 'Chỉ admin mới có thể thay đổi' : ''}
                    />
                  </Form.Item>
                </Col>

                <Col xs={24} sm={12}>
                  <Form.Item
                    label="Giới tính"
                    name="gender"
                  >
                    <Select 
                      placeholder="Chọn giới tính"
                      disabled={user?.role !== 'admin'}
                    >
                      <Option value="male">Nam</Option>
                      <Option value="female">Nữ</Option>
                      <Option value="other">Khác</Option>
                    </Select>
                  </Form.Item>
                </Col>

                <Col xs={24} sm={12}>
                  <Form.Item
                    label="Ngày sinh"
                    name="birthDate"
                  >
                    <DatePicker 
                      placeholder="Chọn ngày sinh"
                      style={{ width: '100%' }}
                      format="DD/MM/YYYY"
                      disabled={user?.role !== 'admin'}
                    />
                  </Form.Item>
                </Col>

                <Col xs={24} sm={12}>
                  <Form.Item
                    label="Số điện thoại"
                    name="phone"
                    rules={[
                      { pattern: /^[0-9]{10,11}$/, message: 'Số điện thoại không hợp lệ!' }
                    ]}
                  >
                    <Input 
                      prefix={<PhoneOutlined />}
                      disabled={user?.role !== 'admin'}
                      placeholder={user?.role !== 'admin' ? 'Chỉ admin mới có thể thay đổi' : ''}
                    />
                  </Form.Item>
                </Col>

                <Col xs={24} sm={12}>
                  <Form.Item
                    label="Quê quán"
                    name="address"
                  >
                    <Input 
                      prefix={<EnvironmentOutlined />}
                      disabled={user?.role !== 'admin'}
                      placeholder={user?.role !== 'admin' ? 'Chỉ admin mới có thể thay đổi' : ''}
                    />
                  </Form.Item>
                </Col>
              </Row>              <Divider />

              <Space>
                <Button 
                  type="primary" 
                  htmlType="submit" 
                  icon={<SaveOutlined />}
                  loading={loading}
                >
                  Lưu thay đổi
                </Button>
                
                <Button 
                  icon={<LockOutlined />}
                  onClick={() => setIsPasswordModalVisible(true)}
                >
                  Đổi mật khẩu
                </Button>
              </Space>
            </Form>
          </Card>
        </Col>
      </Row>      {/* Password Change Modal */}
      <Modal
        title={
          <div style={{ textAlign: 'center', marginBottom: '16px' }}>
            <img 
              src="/logo-evine.png" 
              alt="Evine Logo" 
              style={{ height: '40px', marginBottom: '8px' }}
            />
            <div>Đổi mật khẩu</div>
          </div>
        }
        open={isPasswordModalVisible}
        onCancel={() => {
          console.log('Modal cancelled');
          setIsPasswordModalVisible(false);
          passwordForm.resetFields();        }}
        footer={null}        width={400}
        centered={true}
        destroyOnClose={true}
        maskClosable={true}
        keyboard={true}
        mask={true}
        maskStyle={{ position: 'fixed' }}
        wrapProps={{
          style: { position: 'fixed' }
        }}
      >
        <Form
          form={passwordForm}
          layout="vertical"
          onFinish={handlePasswordChange}
        >
          <Form.Item
            label="Mật khẩu hiện tại"
            name="currentPassword"
            rules={[
              { required: true, message: 'Vui lòng nhập mật khẩu hiện tại!' }
            ]}
          >
            <Input.Password prefix={<LockOutlined />} />
          </Form.Item>

          <Form.Item
            label="Mật khẩu mới"
            name="newPassword"
            rules={[
              { required: true, message: 'Vui lòng nhập mật khẩu mới!' },
              { min: 6, message: 'Mật khẩu phải có ít nhất 6 ký tự!' }
            ]}
          >
            <Input.Password prefix={<LockOutlined />} />
          </Form.Item>

          <Form.Item
            label="Xác nhận mật khẩu mới"
            name="confirmPassword"
            dependencies={['newPassword']}
            rules={[
              { required: true, message: 'Vui lòng xác nhận mật khẩu mới!' },
              ({ getFieldValue }) => ({
                validator(_, value) {
                  if (!value || getFieldValue('newPassword') === value) {
                    return Promise.resolve();
                  }
                  return Promise.reject(new Error('Mật khẩu xác nhận không khớp!'));
                }
              })
            ]}
          >
            <Input.Password prefix={<LockOutlined />} />
          </Form.Item>

          <Form.Item>
            <Space style={{ width: '100%', justifyContent: 'flex-end' }}>
              <Button onClick={() => {
                setIsPasswordModalVisible(false);
                passwordForm.resetFields();
              }}>
                Hủy
              </Button>
              <Button type="primary" htmlType="submit" loading={loading}>
                Đổi mật khẩu
              </Button>
            </Space>
          </Form.Item>        </Form>
      </Modal>
    </div>
  );
};

const Settings: React.FC = () => {
  const [isPasswordModalVisible, setIsPasswordModalVisible] = useState(false);
  
  return (
    <App>
      <SettingsContent 
        isPasswordModalVisible={isPasswordModalVisible}
        setIsPasswordModalVisible={setIsPasswordModalVisible}
      />
    </App>
  );
};

export default Settings;
