import React, { useState, useEffect, useCallback, useRef } from 'react';
import { 
  Card, 
  Typography, 
  Input, 
  Button, 
  List, 
  Avatar, 
  Space,
  Divider,
  Badge,
  Row,
  Col 
} from 'antd';
import { 
  SendOutlined, 
  UserOutlined,
  MessageOutlined 
} from '@ant-design/icons';
import { useAuth } from '../../contexts/AuthContext';
import socketService from '../../services/socketService';
import { chatService, Message, ChatUser } from '../../services/chatService';
import './MessengerChatPage.css';

const { Title, Text } = Typography;
const { TextArea } = Input;

const MessengerChatPage: React.FC = () => {
  const { user, token } = useAuth();
  const [message, setMessage] = useState('');
  const [messages, setMessages] = useState<Message[]>([]);
  const [activeUsers, setActiveUsers] = useState<ChatUser[]>([]);
  const [selectedUser, setSelectedUser] = useState<ChatUser | null>(null);
  const [typing, setTyping] = useState<{ [key: string]: boolean }>({});
  const typingTimeoutRef = useRef<{ [key: string]: NodeJS.Timeout }>({});
  const messagesEndRef = useRef<HTMLDivElement>(null);
  const [socketConnected, setSocketConnected] = useState(false);

  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  };

  const loadMessages = useCallback(async (userId: string) => {
    try {
      const messages = await chatService.getMessages(userId);
      setMessages(messages);
      scrollToBottom();
    } catch (error) {
      console.error('Error loading messages:', error);
    }
  }, []);

  const loadActiveUsers = useCallback(async () => {
    try {
      const users = await chatService.getActiveUsers();
      // Filter out current user
      const filteredUsers = users.filter(u => u._id !== user?._id);
      setActiveUsers(filteredUsers);
    } catch (error) {
      console.error('Error loading active users:', error);
    }
  }, [user?._id]);
  useEffect(() => {
    if (token && !socketConnected) {
      console.log('Connecting socket with token');
      const socket = socketService.connect(`Bearer ${token}`);
      
      if (socket) {
        socket.on('connect', () => {
          console.log('Socket connected successfully');
          setSocketConnected(true);
        });

        socket.on('connect_error', (error: Error) => {
          console.error('Socket connection error:', error);
          setSocketConnected(false);
        });

        socket.on('disconnect', () => {
          console.log('Socket disconnected');
          setSocketConnected(false);
        });
      }

      loadActiveUsers();

      return () => {
        console.log('Cleaning up socket connection');
        socketService.disconnect();
        setSocketConnected(false);
      };
    }
  }, [token, loadActiveUsers]);

  useEffect(() => {
    if (socketConnected) {
      const messageHandler = (newMessage: Message) => {
        console.log('Received message:', newMessage);
        setMessages(prev => {
          // Check if message already exists
          const exists = prev.some(msg => msg._id === newMessage._id);
          if (!exists) {
            return [...prev, newMessage];
          }
          return prev;
        });
        
        // Only scroll if the message is part of the current conversation
        if ((newMessage.sender === user?._id && newMessage.receiver === selectedUser?._id) ||
            (newMessage.sender === selectedUser?._id && newMessage.receiver === user?._id)) {
          scrollToBottom();
        }
      };

      const userStatusHandler = async (userId: string) => {
        await loadActiveUsers();
      };

      const typingHandler = ({ userId, isTyping }: { userId: string, isTyping: boolean }) => {
        setTyping(prev => ({ ...prev, [userId]: isTyping }));
      };

      socketService.onMessage(messageHandler);
      socketService.onUserStatus(userStatusHandler);
      socketService.onTyping(typingHandler);

      return () => {
        // Cleanup event listeners
        socketService.off('newMessage');
        socketService.off('messageConfirmed');
        socketService.off('userOnline');
        socketService.off('userOffline');
        socketService.off('userTyping');
      };
    }
  }, [socketConnected, loadActiveUsers, user?._id, selectedUser?._id]);

  useEffect(() => {
    if (selectedUser) {
      loadMessages(selectedUser._id);
    }
  }, [selectedUser, loadMessages]);
  const handleSendMessage = async () => {
    if (!message.trim() || !selectedUser || !user?._id) return;

    try {
      // Clear typing status
      if (typingTimeoutRef.current[selectedUser._id]) {
        clearTimeout(typingTimeoutRef.current[selectedUser._id]);
      }
      socketService.sendTypingStatus(selectedUser._id, false);

      const content = message.trim();
      setMessage(''); // Reset input right away for better UX

      // Create a temporary message
      const tempMessage: Message = {
        _id: Date.now().toString(),
        content,
        sender: user._id,
        receiver: selectedUser._id,
        messageType: 'text',
        timestamp: new Date(),
        read: false
      };

      // Add optimistic update
      setMessages(prev => [...prev, tempMessage]);
      scrollToBottom();

      // Send through both socket and API for reliability
      socketService.sendMessage(selectedUser._id, content);
      
      try {
        const savedMessage = await chatService.sendMessage(selectedUser._id, content);
        // Replace temp message with saved message
        setMessages(prev => prev.map(msg => 
          msg._id === tempMessage._id ? savedMessage : msg
        ));
      } catch (error) {
        console.error('Failed to save message to API:', error);
        // Keep the temporary message as it was sent through socket
      }
    } catch (error) {
      console.error('Error sending message:', error);
    }
  };

  const handleKeyPress = (e: React.KeyboardEvent) => {
    if (e.key === 'Enter' && !e.shiftKey) {
      e.preventDefault();
      handleSendMessage();
    }
  };

  const handleTyping = (e: React.ChangeEvent<HTMLTextAreaElement>) => {
    setMessage(e.target.value);
    
    if (selectedUser) {
      // Clear existing timeout
      if (typingTimeoutRef.current[selectedUser._id]) {
        clearTimeout(typingTimeoutRef.current[selectedUser._id]);
      }

      // Send typing status
      socketService.sendTypingStatus(selectedUser._id, true);

      // Set new timeout
      typingTimeoutRef.current[selectedUser._id] = setTimeout(() => {
        socketService.sendTypingStatus(selectedUser._id, false);
      }, 2000);
    }
  };

  const formatTime = (date: Date) => {
    return new Date(date).toLocaleTimeString('vi-VN', { 
      hour: '2-digit', 
      minute: '2-digit' 
    });
  };
  const filteredMessages = selectedUser 
    ? messages.filter(msg => 
        (msg.sender === selectedUser._id && msg.receiver === user?._id) ||
        (msg.sender === user?._id && msg.receiver === selectedUser._id)
      )
    : [];

  return (
    <div className="messenger-chat-page">
      <Row gutter={16} style={{ height: '100%' }}>
        <Col xs={24} sm={8} md={6} lg={6} xl={5}>
          <Card 
            title={
              <Space>
                <MessageOutlined />
                <span>Conversations</span>
              </Space>
            }
            className="users-card"
            bodyStyle={{ padding: 0 }}
          >
            <List
              dataSource={activeUsers}
              renderItem={(chatUser) => (
                <List.Item
                  className={`user-item ${selectedUser?._id === chatUser._id ? 'selected' : ''}`}
                  onClick={() => setSelectedUser(chatUser)}
                >
                  <List.Item.Meta
                    avatar={
                      <Badge 
                        dot 
                        status={chatUser.isOnline ? 'success' : 'default'}
                        offset={[-2, 32]}
                      >
                        <Avatar 
                          icon={<UserOutlined />} 
                          src={chatUser.avatar}
                          size="large"
                        />
                      </Badge>
                    }
                    title={<Text strong>{chatUser.username}</Text>}
                    description={
                      typing[chatUser._id] ? (
                        <Text type="success">Typing...</Text>
                      ) : chatUser.isOnline ? (
                        <Text type="success">Online</Text>
                      ) : chatUser.lastSeen ? (
                        <Text type="secondary">
                          Last seen at {formatTime(chatUser.lastSeen)}
                        </Text>
                      ) : null
                    }
                  />
                </List.Item>
              )}
            />
          </Card>
        </Col>

        <Col xs={24} sm={16} md={18} lg={18} xl={19}>
          {selectedUser ? (
            <Card
              className="chat-card"
              title={
                <Space>
                  <Badge 
                    dot 
                    status={selectedUser.isOnline ? 'success' : 'default'}
                    offset={[-2, 32]}
                  >
                    <Avatar 
                      icon={<UserOutlined />} 
                      src={selectedUser.avatar}
                      size="large"
                    />
                  </Badge>
                  <span>{selectedUser.username}</span>
                  {typing[selectedUser._id] && (
                    <Text type="secondary" italic>typing...</Text>
                  )}
                </Space>
              }
              style={{
                height: 'calc(100vh - 200px)'
              }}
            >
              <div className="messages-container">
                <List
                  dataSource={filteredMessages}
                  renderItem={(msg) => (
                    <List.Item
                      className={`message-item ${
                        msg.sender === user?._id ? 'own-message' : 'other-message'
                      }`}
                    >
                      <div className="message-content">
                        <div className="message-bubble">
                          <Text>{msg.content}</Text>
                        </div>
                        <Text type="secondary" className="message-time">
                          {formatTime(msg.timestamp)}
                        </Text>
                      </div>
                    </List.Item>
                  )}
                />
                <div ref={messagesEndRef} />
              </div>

              <Divider style={{ margin: 0 }} />

              <div className="message-input-container">
                <Space.Compact style={{ width: '100%' }}>
                  <TextArea
                    value={message}
                    onChange={handleTyping}
                    onKeyPress={handleKeyPress}
                    placeholder="Type a message..."
                    autoSize={{ minRows: 1, maxRows: 4 }}
                    style={{ resize: 'none' }}
                  />
                  <Button
                    type="primary"
                    icon={<SendOutlined />}
                    onClick={handleSendMessage}
                    disabled={!message.trim()}
                  >
                    Send
                  </Button>
                </Space.Compact>
              </div>
            </Card>
          ) : (
            <Card className="no-chat-selected">
              <div style={{ textAlign: 'center', padding: '60px 20px' }}>
                <MessageOutlined style={{ fontSize: '48px', color: '#d9d9d9' }} />
                <Title level={4} type="secondary">Select a conversation to start chatting</Title>
                <Text type="secondary">
                  Choose from your existing conversations on the left or start a new one.
                </Text>
              </div>
            </Card>
          )}
        </Col>
      </Row>
    </div>
  );
};

export default MessengerChatPage;
