import React, { createContext, useContext, useState, useEffect, ReactNode } from 'react';
import { User, AuthContextType } from '../types';
import { userAPI } from '../services/api';

interface AdminData {
  users: User[];
  lastFetched: number;
}

const CACHE_DURATION = 5 * 60 * 1000; // 5 minutes

const AuthContext = createContext<AuthContextType | undefined>(undefined);

interface AuthProviderProps {
  children: ReactNode;
}

export const AuthProvider: React.FC<AuthProviderProps> = ({ children }) => {
  const [user, setUser] = useState<User | null>(null);
  const [token, setToken] = useState<string | null>(null);
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [adminData, setAdminData] = useState<AdminData>({ users: [], lastFetched: 0 });

  useEffect(() => {
    // Check if user is logged in on app start
    const savedUser = localStorage.getItem('user');
    const savedToken = localStorage.getItem('token');
    
    if (savedUser && savedToken) {
      const parsedUser = JSON.parse(savedUser);
      setUser(parsedUser);
      setToken(savedToken);
      setIsAuthenticated(true);

      // If user is admin, preload data
      if (parsedUser.role === 'admin') {
        preloadAdminData();
      }
    }
  }, []);

  const preloadAdminData = async () => {
    try {
      // Check if cache is still valid
      const now = Date.now();
      if (now - adminData.lastFetched < CACHE_DURATION && adminData.users.length > 0) {
        return;
      }

      const usersData = await userAPI.getAllUsers();
      setAdminData({
        users: usersData,
        lastFetched: now
      });
    } catch (error) {
      console.error('Error preloading admin data:', error);
    }
  };

  const login = async (userData: User, accessToken: string) => {
    // Update state first for immediate UI response
    setUser(userData);
    setToken(accessToken);
    setIsAuthenticated(true);

    // Then update localStorage in background
    Promise.all([
      localStorage.setItem('user', JSON.stringify(userData)),
      localStorage.setItem('token', accessToken)
    ]).catch(console.error);

    // Load admin data only if needed
    if (userData.role === 'admin') {
      preloadAdminData().catch(console.error);
    }
  };

  const logout = () => {
    // Clear state immediately
    setUser(null);
    setToken(null);
    setIsAuthenticated(false);
    setAdminData({ users: [], lastFetched: 0 });

    // Clear localStorage in background
    Promise.all([
      localStorage.removeItem('user'),
      localStorage.removeItem('token')
    ]).catch(console.error);
  };

  const updateUser = (updatedUserData: Partial<User>) => {
    if (user) {
      const newUser = { ...user, ...updatedUserData };
      setUser(newUser);
      localStorage.setItem('user', JSON.stringify(newUser));
    }
  };

  const refreshUserData = async () => {
    if (user && token) {
      try {
        const userData = await userAPI.getCurrentUser();
        updateUser(userData);
        // Clear admin data cache when refreshing
        setAdminData({ users: [], lastFetched: 0 });
        if (userData.role === 'admin') {
          await preloadAdminData();
        }
      } catch (error) {
        console.error('Error refreshing user data:', error);
      }
    }
  };

  // Add new function to clear cache
  const clearCache = () => {
    setAdminData({ users: [], lastFetched: 0 });
  };

  const getUsers = () => {
    return adminData.users;
  };

  const refreshUsers = async () => {
    if (user?.role === 'admin') {
      await preloadAdminData();
    }
  };

  // Modify value object to include clearCache
  const value: AuthContextType = {
    user,
    isAuthenticated,
    login,
    logout,
    updateUser,
    token,
    refreshUserData,
    getUsers,
    refreshUsers,
    clearCache,
  };

  return (
    <AuthContext.Provider value={value}>
      {children}
    </AuthContext.Provider>
  );
};

export const useAuth = (): AuthContextType => {
  const context = useContext(AuthContext);
  if (context === undefined) {
    throw new Error('useAuth must be used within an AuthProvider');
  }
  return context;
};
